import './shop.css'

export default function ShopCart( product) {
	return (
		<>
			<div className="card-category-item mt-0 mb-0">
				<a>
					<img className="img-fluid" src={product.src} alt="" />
					
				    <h5> {product.description} </h5>
					<h6>
					{product.price}
					</h6>
					<button className="btn btn-sm btn-success">Add to Cart</button>
				</a>
			</div>
		</>
	);
}
