import { useState } from "react";
import { Link, useHistory } from "react-router-dom";

import "./Navigation.css";

import Navbar from "react-bootstrap/Navbar";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import FormControl from "react-bootstrap/FormControl";
import InputGroup from "react-bootstrap/InputGroup";
import NavDropdown from "react-bootstrap/NavDropdown";
import Dropdown from "react-bootstrap/Dropdown";
import Badge from "react-bootstrap/Badge";

import LogoutModal from "./LogoutModal";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
	faBars,
	faPlusCircle,
	faHeadphonesAlt,
	faStar,
	faEdit,
	faSignOutAlt,
	faUserCircle,
	faVideo,
	faCog,
	faSearch,
	faBell,
	faEnvelope,
} from "@fortawesome/free-solid-svg-icons";

const Navigation = ({ props }) => {
	const [show, setShow] = useState(false);

	const history = useHistory();

	const handleClose = () => setShow(false);
	const handleShow = () => setShow(true);

	const handleLogout = (e) => {
		e.preventDefault();
		handleClose();
		history.push("/auth/login");
	};

	const handleMenuToggle = () => {
		props.setShowMenu(!props.showMenu);
	};

	return (
		<>
			<Navbar bg="white" fixed="bottom" className="osahan-nav">
				&nbsp;&nbsp;
				<Button
					variant="link"
					size="sm"
					className="order-1 order-sm-0 text-secondary"
					id="sidebarToggle"
					onClick={handleMenuToggle}
				>
					<FontAwesomeIcon icon={faBars} />
					<i className="fas fa-star"></i>
				</Button>
				&nbsp;&nbsp;
				<Navbar.Brand className="mr-1">
					<Link to="/">
						<img className="img-fluid" src="/img/logo.jpg" alt="" style={{width:'150px'}} />
					</Link>
				</Navbar.Brand>
				{/* Navbar Search*/}
				<Form
					inline
					className="d-none d-md-inline ml-auto  my-2 my-md-0 osahan-navbar-search"
				>
					<InputGroup>
						<FormControl
							type="text"
							placeholder="Search for..."
							className=""
						/>
						<InputGroup.Append>
							<Button variant="light">
								<FontAwesomeIcon icon={faSearch} />
							</Button>
						</InputGroup.Append>
					</InputGroup>
				</Form>
				{/*Navbar*/}
				<ul className="navbar-nav ml-auto ml-md-0 osahan-right-navbar">
					<li className="nav-item mx-1">
						<Link to="/upload" className="nav-link">
							<FontAwesomeIcon icon={faPlusCircle} fixedWidth />{" "}
							<span className="d-none d-md-inline">
								Upload Video
							</span>
						</Link>
					</li>

					<NavDropdown
						title={
							<>
								<FontAwesomeIcon icon={faBell} fixedWidth />
								<Badge variant="danger">9+</Badge>
							</>
						}
						id=""
						className="mx-1 no-arrow"
					>
						<NavDropdown.Item href="#">
							<FontAwesomeIcon icon={faEdit} fixedWidth /> Action
						</NavDropdown.Item>
						<NavDropdown.Item href="#">
							<FontAwesomeIcon
								icon={faHeadphonesAlt}
								fixedWidth
							/>{" "}
							Another Action
						</NavDropdown.Item>
						<Dropdown.Divider />
						<NavDropdown.Item href="#">
							<FontAwesomeIcon icon={faStar} fixedWidth />{" "}
							Something else here
						</NavDropdown.Item>
					</NavDropdown>

					<NavDropdown
						title={
							<>
								<FontAwesomeIcon icon={faEnvelope} fixedWidth />
								<Badge variant="success">7</Badge>
							</>
						}
						id=""
						className="mx-1 no-arrow"
					>
						<NavDropdown.Item href="#">
							<FontAwesomeIcon icon={faEdit} fixedWidth /> Action
						</NavDropdown.Item>
						<NavDropdown.Item href="#">
							<FontAwesomeIcon
								icon={faHeadphonesAlt}
								fixedWidth
							/>{" "}
							Another Action
						</NavDropdown.Item>
						<Dropdown.Divider />
						<NavDropdown.Item href="#">
							<FontAwesomeIcon icon={faStar} fixedWidth />{" "}
							Something else here
						</NavDropdown.Item>
					</NavDropdown>

					<NavDropdown
						title={
							<>
								<img src="/img/user.png" alt="Avatar" />
								<span className="d-none d-md-inline">
									{" "}
									Osahan
								</span>
							</>
						}
						id=""
						className="no-arrow osahan-right-navbar-user user-dropdown-link"
					>
						<Link
							to="/account"
							className="dropdown-item"
							rolw="button"
						>
							<FontAwesomeIcon icon={faUserCircle} fixedWidth />{" "}
							My Account
						</Link>
						<Link
							to="/subscriptions"
							className="dropdown-item"
							rolw="button"
						>
							<FontAwesomeIcon icon={faVideo} fixedWidth />{" "}
							Subscriptions
						</Link>
						<Link
							to="/settings"
							className="dropdown-item"
							rolw="button"
						>
							<FontAwesomeIcon icon={faCog} fixedWidth /> Settings
						</Link>
						<Dropdown.Divider />
						<Link
							to="#"
							onClick={handleShow}
							className="dropdown-item"
							rolw="button"
						>
							<FontAwesomeIcon icon={faSignOutAlt} fixedWidth />{" "}
							Log out
						</Link>
					</NavDropdown>
				</ul>
			</Navbar>

			<LogoutModal
				show={show}
				handleClose={handleClose}
				handleLogout={handleLogout}
			/>
		</>
	);
};

export default Navigation;
