},
]
const productLink="http://localhost:3000/hulusthul/Shop"

const Shop = () => {
	
	return (
		<>
			<ContentWrapper>
				<Container fluid>
					<div className="video-block section-padding">
						<Row>
                        <Col md={12}>
								<SectionHeader heading="Shop" />
							</Col>
                            
								

								  {
								 
								 products.map((product)=>(

									<Col xl={3} sm={6} className="mb-3" 	key={product.id} >
									
									<ShopCart
								
								product={product}
									/>
									  </Col>

								  ))} 
								
						</Row>
					</div>
					<Paginate />
				</Container>
				<ThinFooter />
			</ContentWrapper>
		</>
	);
};

export default Shop;
