import './shop.css'

export default function ShopCart({
 
	href = "#",
	description,
	price,
	src
}) {
	return (
		<>
			<div className="card-category-item mt-0 mb-0">
				<a href={href}>
					<img className="img-fluid" src={src} alt="" />
					
				    <h5> {description} </h5>
					<h6>
					{price}
					</h6>
					<button className="btn btn-sm btn-success">Add to Cart</button>
				</a>
			</div>
		</>
	);
}
